import React, { useRef, useState, useCallback } from 'react';
import Webcam from 'react-webcam';
import axios from 'axios';
import './App.css';
import Dashboard from './components/Dashboard';

const App: React.FC = () => {
  const webcamRef = useRef<Webcam>(null);
  const [recording, setRecording] = useState(false);
  const mediaRecorderRef = useRef<MediaRecorder | null>(null);
  const [recordedChunks, setRecordedChunks] = useState<Blob[]>([]);

  const [audioRecording, setAudioRecording] = useState(false);
  const [audioBlob, setAudioBlob] = useState<Blob | null>(null);

  const handleDataAvailable = useCallback(({ data }: BlobEvent) => {
    if (data.size > 0) {
      setRecordedChunks((prev) => prev.concat(data));
    }
  }, []);

  const handleStartCaptureClick = useCallback(() => {
    if (webcamRef.current && webcamRef.current.stream) {
      setRecording(true);
      mediaRecorderRef.current = new MediaRecorder(webcamRef.current.stream, { mimeType: 'video/webm' });
      mediaRecorderRef.current.addEventListener('dataavailable', handleDataAvailable);
      mediaRecorderRef.current.start();
    }
  }, [handleDataAvailable]);

  const handleStopCaptureClick = useCallback(() => {
    if (mediaRecorderRef.current) {
      mediaRecorderRef.current.stop();
      setRecording(false);
    }
  }, []);

  const handleUpload = useCallback(async () => {
    if (recordedChunks.length) {
      const blob = new Blob(recordedChunks, { type: 'video/webm' });
      const formData = new FormData();
      formData.append('video', blob, 'recorded_video.webm');

      try {
        const response = await axios.post('http://localhost:8000/upload', formData);
        console.log('Video uploaded successfully:', response.data);
        setRecordedChunks([]);
      } catch (error) {
        console.error('Error uploading video:', error);
      }
    }
  }, [recordedChunks]);

  const handleStartAudioRecording = () => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state === 'inactive') {
      mediaRecorderRef.current.start();
    }
  };

  const handleStopAudioRecording = async (): Promise<Blob | undefined> => {
    if (mediaRecorderRef.current) {
      return new Promise((resolve) => {
        mediaRecorderRef.current!.stop();
        mediaRecorderRef.current!.addEventListener('dataavailable', (event) => {
          if (event.data.size > 0) {
            resolve(event.data);
          } else {
            resolve(undefined);
          }
        });
      });
    }
    return undefined;
  };

  const handleAudioUpload = useCallback(async () => {
    if (audioBlob) {
      const formData = new FormData();
      formData.append('audio', audioBlob, 'recorded_audio.wav');

      try {
        const response = await axios.post('http://localhost:8000/upload-audio', formData);
        console.log('Audio uploaded and transcribed successfully:', response.data);
        setAudioBlob(null);
      } catch (error) {
        console.error('Error uploading and transcribing audio:', error);
      }
    }
  }, [audioBlob]);

  return (
    <div className="App">
      <h1>Video Recording App</h1>
      <Webcam 
        audio={true}
        videoConstraints={{
          width: 1280,
          height: 720,
          facingMode: "user"
        }}
        ref={webcamRef}
      />
      <div>
        {recording ? (
          <button onClick={handleStopCaptureClick}>Stop Video Recording</button>
        ) : (
          <button onClick={handleStartCaptureClick}>Start Video Recording</button>
        )}
        {recordedChunks.length > 0 && (
          <button onClick={handleUpload}>Upload Video</button>
        )}
      </div>
      <div>
        {audioRecording ? (
          <button onClick={handleStopAudioRecording}>Stop Audio Recording</button>
        ) : (
          <button onClick={handleStartAudioRecording}>Start Audio Recording</button>
        )}
        {audioBlob && (
          <button onClick={handleAudioUpload}>Upload Audio</button>
        )}
      </div>
      <Dashboard onAudioRecordingStart={handleStartAudioRecording} onAudioRecordingStop={handleStopAudioRecording} />
    </div>
  );
};

export default App;
