import React, { useRef, useState, useCallback } from 'react';
import Webcam from 'react-webcam';
import axios from 'axios';
import './App.css';
import Dashboard from './components/Dashboard';

const App: React.FC = () => {
  const webcamRef = useRef<Webcam>(null);
  const [recording, setRecording] = useState(false);
  const mediaRecorderRef = useRef<MediaRecorder | null>(null);
  const [recordedChunks, setRecordedChunks] = useState<Blob[]>([]);

  const handleDataAvailable = useCallback(({ data }: BlobEvent) => {
    if (data.size > 0) {
      setRecordedChunks((prev) => prev.concat(data));
    }
  }, []);

  const handleStartCaptureClick = useCallback(() => {
    if (webcamRef.current && webcamRef.current.stream) {
      setRecording(true);
      mediaRecorderRef.current = new MediaRecorder(webcamRef.current.stream, { mimeType: 'video/webm' });
      mediaRecorderRef.current.addEventListener('dataavailable', handleDataAvailable);
      mediaRecorderRef.current.start();
    }
  }, [handleDataAvailable]);

  const handleStopCaptureClick = useCallback(() => {
    if (mediaRecorderRef.current) {
      mediaRecorderRef.current.stop();
      setRecording(false);
    }
  }, []);

  const handleUpload = useCallback(async () => {
    if (recordedChunks.length) {
      const blob = new Blob(recordedChunks, { type: 'video/webm' });
      const formData = new FormData();
      formData.append('video', blob, 'recorded_video.webm');

      try {
        const response = await axios.post('http://localhost:8000/upload', formData);
        console.log('Video uploaded successfully:', response.data);
        setRecordedChunks([]);
      } catch (error) {
        console.error('Error uploading video:', error);
      }
    }
  }, [recordedChunks]);

  return (
    <div className="App">
      <h1>Video Recording App</h1>
      <Webcam 
        audio={true}
        videoConstraints={{
          width: 1280,
          height: 720,
          facingMode: "user"
        }}
        ref={webcamRef}
      />
      <div>
        {recording ? (
          <button onClick={handleStopCaptureClick}>Stop Recording</button>
        ) : (
          <button onClick={handleStartCaptureClick}>Start Recording</button>
        )}
        {recordedChunks.length > 0 && (
          <button onClick={handleUpload}>Upload</button>
        )}
      </div>
      <Dashboard />
    </div>
  );
};

export default App;
