import React from 'react';

interface FloatingNoteBoxProps {
  content: string;
  position: { x: number; y: number };
}

const FloatingNoteBox: React.FC<FloatingNoteBoxProps> = ({ content, position }) => {
  const comments = content.split('\n\n');

  return (
    <div
      style={{
        position: 'fixed',
        left: `${position.x}px`,
        top: `${position.y}px`,
        transform: 'translate(-50%, -100%)',
        backgroundColor: 'white',
        border: '1px solid #ccc',
        borderRadius: '4px',
        padding: '12px',
        boxShadow: '0 2px 10px rgba(0,0,0,0.2)',
        zIndex: 1000,
        maxWidth: '300px',
        minWidth: '200px',
        fontSize: '14px',
        lineHeight: '1.4',
      }}
    >
      {comments.map((comment, index) => (
        <div key={index} style={{ marginBottom: index < comments.length - 1 ? '10px' : '0' }}>
          {comment.split('\n').map((line, lineIndex) => (
            <p key={lineIndex} style={{ margin: '0', whiteSpace: 'pre-wrap', wordBreak: 'break-word' }}>{line}</p>
          ))}
        </div>
      ))}
    </div>
  );
};

export default FloatingNoteBox;
