import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Dashboard.css';


interface Utterance {
  speaker: string;
  text: string;
}

interface Transcript {
  transcriptId: string;
  content: string;
  fileType: string;
}

interface DashboardProps {
  onAudioRecordingStart: () => void;
  onAudioRecordingStop: () => Promise<Blob | null>;
}

const extractTextContent = (content: Utterance[] | string): string => {
  if (Array.isArray(content)) {
    return content.map(utterance => utterance.text).join(' ');
  } else if (typeof content === 'string') {
    try {
      const parsedContent = JSON.parse(content);
      if (parsedContent.results && parsedContent.results.channels) {
        return parsedContent.results.channels[0].alternatives[0].transcript;
      }
    } catch (error) {
      // If parsing fails, return the original string
    }
    return content;
  }
  return 'No content available';
};

const Dashboard: React.FC<DashboardProps> = ({ onAudioRecordingStart, onAudioRecordingStop }) => {
  const [transcripts, setTranscripts] = useState<Transcript[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [isRecording, setIsRecording] = useState(false);
  const [selectedTranscript, setSelectedTranscript] = useState<Transcript | null>(null);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [selectedText, setSelectedText] = useState({ start: 0, end: 0, text: '' });
  const [comments, setComments] = useState<Array<{
    commentId: string;
    content: string;
    type: string;
    fileAttachment?: string;
  }>>([]);
  const [commentType, setCommentType] = useState('positive');
  const [commentFile, setCommentFile] = useState<File | null>(null);
  const [commentContent, setCommentContent] = useState('');

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    if (event.target.files) {
      setSelectedFile(event.target.files[0]);
    }
  };

  const handleFileUpload = async () => {
    if (!selectedFile) {
      setError('Please select a file to upload.');
      return;
    }

    const formData = new FormData();
    formData.append('transcript', selectedFile);

    try {
      const response = await axios.post('http://localhost:8000/upload-transcript', formData);
      console.log('Transcript uploaded successfully:', response.data);
      setSelectedFile(null);
      fetchTranscripts(); // Refresh the transcript list
    } catch (error) {
      console.error('Error uploading transcript:', error);
      if (axios.isAxiosError(error) && error.response) {
        setError(`Failed to upload transcript: ${error.response.data.message || error.message}`);
      } else {
        setError('Failed to upload transcript. Please try again.');
      }
    }
  };

  useEffect(() => {
    fetchTranscripts();
  }, []);

  const fetchTranscripts = async () => {
    try {
      setLoading(true);
      const response = await axios.get<Transcript[]>('http://localhost:8000/api/transcripts');
      const formattedTranscripts = response.data.map((transcript) => {
        const extractedContent = extractTextContent(transcript.content);
        console.log('Extracted content:', extractedContent);
        return {
          transcriptId: transcript.transcriptId.split('/').pop() || 'Unnamed Transcript',
          content: extractedContent,
          fileType: transcript.fileType
        };
      });
      setTranscripts(formattedTranscripts);
      setError(null);
    } catch (error) {
      console.error('Error fetching transcripts:', error);
      setError('Failed to fetch transcripts. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const handleRecordButtonClick = async () => {
    if (isRecording) {
      const audioBlob = await onAudioRecordingStop();
      setIsRecording(false);
      if (audioBlob) {
        await uploadAudio(audioBlob);
      }
    } else {
      onAudioRecordingStart();
      setIsRecording(true);
    }
  };

  const uploadAudio = async (audioBlob: Blob) => {
    const formData = new FormData();
    formData.append('audio', audioBlob, 'recorded_audio.wav');
    try {
      const response = await axios.post('http://localhost:8000/upload-audio', formData);
      console.log('Audio uploaded and transcribed successfully:', response.data);
      fetchTranscripts(); // Refresh the transcript list
    } catch (error) {
      console.error('Error uploading and transcribing audio:', error);
      if (axios.isAxiosError(error) && error.response) {
        setError(`Failed to upload and transcribe audio: ${error.response.data.message || error.message}`);
      } else {
        setError('Failed to upload and transcribe audio. Please try again.');
      }
    }
  };

  const handleTranscriptClick = (transcript: Transcript) => {
    setSelectedTranscript(transcript);
  };

  const handleTextSelection = () => {
    const selection = window.getSelection();
    if (selection && selection.rangeCount > 0) {
      const range = selection.getRangeAt(0);
      const start = range.startOffset;
      const end = range.endOffset;
      const text = selection.toString();
      setSelectedText({ start, end, text });
    }
  };

  const addComment = async () => {
    if (!selectedTranscript || !commentContent) return;

    const formData = new FormData();
    formData.append('transcriptId', selectedTranscript.transcriptId);
    formData.append('userId', 'currentUserId'); // Replace with actual user ID
    formData.append('content', commentContent);
    formData.append('timestamp', new Date().toISOString());
    formData.append('startIndex', selectedText.start.toString());
    formData.append('endIndex', selectedText.end.toString());
    formData.append('type', commentType);
    if (commentFile) {
      formData.append('fileAttachment', commentFile);
    }

    try {
      const response = await axios.post('http://localhost:8000/api/comment', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      setComments(prevComments => [...prevComments, response.data.comment]);
      // Clear the comment content and file after adding a comment
      setCommentContent('');
      setCommentFile(null);
      setSelectedText({ start: 0, end: 0, text: '' });
    } catch (error) {
      console.error('Error adding comment:', error);
      setError('Failed to add comment. Please try again.');
    }
  };

  const deleteComment = async (commentId: string) => {
    if (!selectedTranscript) return;
    try {
      await axios.delete(`http://localhost:8000/api/comment`, {
        data: { transcriptId: selectedTranscript.transcriptId, commentId }
      });
      setComments(prevComments => prevComments.filter(comment => comment.commentId !== commentId));
    } catch (error) {
      console.error('Error deleting comment:', error);
      setError('Failed to delete comment. Please try again.');
    }
  };

  const updateComment = async (commentId: string, newContent: string, newType: string) => {
    if (!selectedTranscript) return;
    try {
      const requestData = {
        transcriptId: selectedTranscript.transcriptId,
        commentId,
        content: newContent,
        type: newType
      };
      console.log('Updating comment with data:', requestData);
      const response = await axios.put(`http://localhost:8000/api/comment`, requestData, {
        headers: {
          'Content-Type': 'application/json'
        }
      });
      if (response.data && response.data.updatedAttributes) {
        setComments(prevComments => prevComments.map(comment => 
          comment.commentId === commentId ? {...comment, ...response.data.updatedAttributes} : comment
        ));
      }
    } catch (error) {
      console.error('Error updating comment:', error);
      setError('Failed to update comment. Please try again.');
    }
  };

  useEffect(() => {
    if (selectedTranscript) {
      fetchCommentsForTranscript(selectedTranscript.transcriptId);
    }
  }, [selectedTranscript]);

  const fetchCommentsForTranscript = async (transcriptId: string) => {
    try {
      const response = await axios.get(`http://localhost:8000/api/comment?transcriptId=${transcriptId}`);
      setComments(response.data);
    } catch (error) {
      console.error('Error fetching comments:', error);
      setError('Failed to fetch comments. Please try again.');
    }
  };

  return (
    <div className="dashboard">
      <h1>Transcripts Dashboard</h1>
      <button onClick={handleRecordButtonClick}>
        {isRecording ? 'Stop Recording' : 'Start Recording'}
      </button>
      <div className="file-upload">
        <input type="file" onChange={handleFileChange} accept=".txt,.json,.pdf" />
        <button onClick={handleFileUpload} disabled={!selectedFile}>
          Upload Transcript
        </button>
      </div>
      {error && <p className="error">{error}</p>}
      {loading ? (
        <p>Loading transcripts...</p>
      ) : (
        <div className="transcript-list">
          {transcripts.map((transcript) => (
            <div
              key={transcript.transcriptId}
              className="transcript-item"
              onClick={() => handleTranscriptClick(transcript)}
            >
              <h3>{transcript.transcriptId}</h3>
              <p>{transcript.content}</p>
            </div>
          ))}
        </div>
      )}
      {selectedTranscript && (
        <div className="modal">
          <div className="modal-content">
            <h2>{selectedTranscript.transcriptId}</h2>
            <div onMouseUp={handleTextSelection}>
              {extractTextContent(selectedTranscript.content)}
            </div>
            <div>
              <textarea
                value={commentContent}
                onChange={(e) => setCommentContent(e.target.value)}
                placeholder="Enter your comment"
              />
              <select value={commentType} onChange={(e) => setCommentType(e.target.value)}>
                <option value="positive">Positive</option>
                <option value="negative">Negative</option>
                <option value="suggestion">Suggestion</option>
              </select>
              <input type="file" onChange={(e) => e.target.files && setCommentFile(e.target.files[0])} />
              <button onClick={addComment}>Add Comment</button>
            </div>
            <div>
              {comments.map(comment => (
                <div key={comment.commentId} style={{
                  backgroundColor: comment.type === 'negative' ? 'lightcoral' : comment.type === 'positive' ? 'lightgreen' : 'lightyellow',
                  padding: '10px',
                  margin: '10px 0'
                }}>
                  <p>{comment.content}</p>
                  <p>Type: {comment.type}</p>
                  {comment.fileAttachment && <a href={comment.fileAttachment}>Attached File</a>}
                  <button onClick={() => deleteComment(comment.commentId)}>Delete</button>
                  <button onClick={() => {
                    const newContent = prompt('Enter new comment content:', comment.content);
                    const newType = prompt('Enter new comment type (positive, negative, suggestion):', comment.type);
                    if (newContent && newType) {
                      updateComment(comment.commentId, newContent, newType);
                    }
                  }}>Edit</button>
                </div>
              ))}
            </div>
            <button onClick={() => setSelectedTranscript(null)}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
