import React from 'react';

interface FloatingNoteBoxProps {
  content: string;
  type: string;
  position: { x: number; y: number };
}

const FloatingNoteBox: React.FC<FloatingNoteBoxProps> = ({ content, type, position }) => {
  const backgroundColor = type === 'negative' ? 'lightcoral' : type === 'positive' ? 'lightgreen' : 'lightyellow';

  return (
    <div
      style={{
        position: 'fixed',
        left: `${position.x}px`,
        top: `${position.y}px`,
        transform: 'translate(-50%, -100%)',
        backgroundColor,
        border: '1px solid #ccc',
        borderRadius: '4px',
        padding: '12px',
        boxShadow: '0 2px 10px rgba(0,0,0,0.2)',
        zIndex: 1000,
        maxWidth: '300px',
        minWidth: '200px',
        fontSize: '14px',
        lineHeight: '1.4',
      }}
    >
      <p style={{ margin: 0, whiteSpace: 'pre-wrap', wordBreak: 'break-word' }}>{content}</p>
    </div>
  );
};

export default FloatingNoteBox;
